function beginScan(n){let t=new Instascan.Scanner({video:document.getElementById("preview")});t.addListener("scan",function(i){console.log(i);n.invokeMethodAsync("Validate",i).then(n=>{n&&t.stop()})});Instascan.Camera.getCameras().then(function(n){n.length>0?t.start(n[0]):console.error("No cameras found.")}).catch(function(n){console.error(n)})}